using UnityEngine;public class Glass3Setup : MonoBehaviour{	public Shader m_Sdr;	[Range(-0.03f, 0.03f)] public float m_RefractionDensity = -0.03f;	public bool m_Bump = false;	public Texture2D m_BumpTex;	public float m_BumpTexScale = 6f;	Renderer m_Rd;		void Start ()	{		m_Rd = GetComponent<Renderer> ();		Material[] mats = m_Rd.materials;		for (int i = 0; i < mats.Length; i++)		{			mats[i].shader = m_Sdr;			mats[i].DisableKeyword ("CRYSTAL_GLASS_BUMP");			mats[i].SetFloat ("_Density", m_RefractionDensity);		}	}	void Update ()	{		Material[] mats = m_Rd.materials;		for (int i = 0; i < mats.Length; i++)		{			if (m_Bump)				mats[i].EnableKeyword ("CRYSTAL_GLASS_BUMP");			else				mats[i].DisableKeyword ("CRYSTAL_GLASS_BUMP");			mats[i].SetFloat ("_Density", m_RefractionDensity);			mats[i].SetTexture ("_BumpMap", m_BumpTex);			mats[i].SetTextureScale ("_BumpMap", new Vector2 (m_BumpTexScale, m_BumpTexScale));		}	}}